mapper.sql:
		from
			t_multi_key_detail

		
			inner join
				t_header
			on
				t_header.header_no = t_multi_key_detail.header_no
		

		
			
				
					and ${key} = #{field.value}
				
				
					and ${key} <> #{field.notValue}
				
				
					and ${key} is null
				
				
					and ${key} is not null
				
				
					and ${key} like #{field.prefixValue} || '%'
				
				
					and ${key} not like #{field.notPrefixValue} || '%'
				
				
					and ${key} like '%' || #{field.suffixValue}
				
				
					and ${key} not like '%' || #{field.notSuffixValue}
				
				
					and ${key} like '%' || #{field.broadValue} || '%'
				
				
					and ${key} not like '%' || #{field.notBroadValue} || '%'
				
				
					and ${key} < #{field.lowerThanValue}
				
				
					and ${key} <= #{field.lowerEqualValue}
				
				
					and ${key} > #{field.greaterThanValue}
				
				
					and ${key} >= #{field.greaterEqualValue}

mapper.insert:

	select coalesce(max(detail_no),0)+1 from t_multi_key_detail where header_no = #{header.headerNo, jdbcType=BIGINT}

insert into
	t_multi_key_detail (
		header_no,
		detail_no,
		detail_value
	) values (
		#{header.headerNo, jdbcType=BIGINT},
		#{detailNo, jdbcType=BIGINT},
		#{detailValue, jdbcType=VARCHAR}
	)

mapper.update:
update
	t_multi_key_detail
set
	detail_value = #{detailValue, jdbcType=VARCHAR}
where
	header_no = #{header.headerNo, jdbcType=BIGINT}
	and detail_no = #{detailNo, jdbcType=BIGINT}

mapper.delete:
delete from
	t_multi_key_detail
where
	header_no = #{header.headerNo, jdbcType=BIGINT}
	and detail_no = #{detailNo, jdbcType=BIGINT}
